import React, { useEffect, useRef } from 'react';
import { styled } from 'styled-components';
import { useState } from 'react';
import { useParams } from 'react-router-dom';

const Body = styled.body`
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  margin-top: 100px;
`;

const Container = styled.div`
  display: flex;
  width: 100%;
  height: 100%;
  margin: 0 500px;
  margin-top: 80px;
`;

const Img = styled.img`
width: 60%;
  display: flex;
`;

const ProductInfo = styled.div`
  border-top: 2px solid #000;
  margin: 0 20px;
  display: flex;
  flex-direction: column;
  width: 40%;
  .title {
    font-size: 30px;
    font-weight: bold;
    margin: 20px 0;
    display: flex;
    justify-content: space-between;
    align-items: center;
  }
  .origin-price {
    color: #ccc;
    font-size: 18px;
    text-decoration: line-through;
  }
  .price {
    margin-bottom: 30px;
    font-weight: bold;
    font-size: 20px;
    b {
      color: red;
      margin-right: 10px;
    }
  }
  .benefitBox {
    background-color: #eee;
    width: 100%;
    height: 70px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 0 20px;
    font-weight: bold;
    b{
      color: red;
    }
    .go {
      font-size: 20px;
      color: red;
    }
  }
`;

const ImgContainer = styled.div`
  display: flex;
  margin: 30px 0;
  img {
    width: 100px;
    margin-right: 20px; 
  }
`;

const Size = styled.div`
  span {
    border: 1px solid #ccc;
    padding: 5px 10px;
    margin-right: 10px;
    cursor: pointer;
  }
`;

const SelectItem = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  border-top: 1px solid #eee;
  margin-top: 50px;
  padding: 20px 0;
  .select_size {
    display: flex;
    flex-direction: column;
    justify-content: center;
    h4 {
      font-weight: bold;
      font-size: 16px;
    }
    span {
      color: #999;
    }
  }
  input {
    width: 50px;
    border: 1px solid #ccc;
    margin-right: 10px;
  }
  button {
    border: 1px solid #ccc;
  }
`;

const TotalPrice = styled.div`
  display: flex;
  justify-content: space-between;
  aling-items: center;
  border-top: 1px solid #999;
  padding: 20px 0;
  span:first-child {
    font-size: 22px;
  }
  span:last-child {
    font-size: 18px;
    font-weight: bold;
  }
`;


const ProductDetail = () => {
  let {id} = useParams();
  let countRef = useRef();
  console.log(countRef);
  const [product, setProduct] = useState([]);
  const getProduct = async () => {
    // let url = `http://localhost:3005/products/${id}`;
    let url = `https://my-json-server.typicode.com/sungdongyoon/whatitisnt/products/${id}`;
    let response = await fetch(url);
    let data = await response.json();
    setProduct(data);
  };
  useEffect(() => {
    getProduct();
  }, [])
  return (
    <Body>
      <Container>
        <Img src={product.img}/>
        <ProductInfo>
          <div className='title'>
            <span>{product.title}</span>
            <img src='https://wiisnt.co.kr/images/cm_icon_socials.gif'/>
          </div>
          <div className='origin-price'>{product.price}원</div>
          <div className='price'><b>{product.discount}%</b>{product.price - ((product.price) * ((product.discount)/100))}원</div>
          <div className='benefitBox'>
            <span>공식 홈페이지 <b>회원혜택</b> 확인하기</span>
            <span className='go'>Go</span>
          </div>
          <ImgContainer>
            <img src={product.img}/>
            <img src={product.img}/>
            <img src={product.img}/>
            <img src={product.img}/>
          </ImgContainer>
          <Size>
            <span>{product.size}</span>
            <span>{product.size}</span>
            <span>{product.size}</span>
            {/* {product.size.map((it) => (
              <span>{it}</span>
            ))} */}
          </Size>
          <SelectItem>
            <div className='select_size'>
              <h4>{product.title}</h4>
              <span>{product.size}</span>
            </div>
            <div>
              <input ref={countRef} value={countRef.current} type='number' min="1"/>
              <button>x</button>
            </div>
            <div className='item_price'>
              <span>{product.price}</span>
            </div>
          </SelectItem>
          <TotalPrice>
            <span>총 상품금액</span>
            <span>0원(0개)</span>
          </TotalPrice>
        </ProductInfo>
      </Container>
    </Body>
    
  )
}

export default ProductDetail;
